#!/bin/bash
source sc_bash.sh


echo
echo
echo "Test: create contract =================================================="

fab_bal $DAEMON_NUM 

port_rpc=$(($RPC_PORT + 4 ))
export TXID_4_1=`${FAB_ROOT}/src/fabcoin-cli -regtest -rpcuser=fabcoinrpc -rpcpassword=P0 -rpcconnect=localhost:${port_rpc} sendtoaddress ${ADDR_1} 10`
echo "TXID (node 4-->1) 10 coin" $TXID_4_1

port_rpc=$(($RPC_PORT + 3 ))
export TXID_3_4=`${FAB_ROOT}/src/fabcoin-cli -regtest -rpcuser=fabcoinrpc -rpcpassword=P0 -rpcconnect=localhost:${port_rpc} sendtoaddress ${ADDR_4} 10`
echo "TXID (node 3-->4) 10 coin" $TXID_3_4
echo "Above transaction is for node 4 creating contract"

sleep 2
fab_mining_one 5 $FAB_ROOT $RPC_PORT 5 
sleep 5

fab_bal $DAEMON_NUM 

port_rpc=$(($RPC_PORT + 4 ))
  
export SC_4=`${FAB_ROOT}/src/fabcoin-cli -regtest -rpcuser=fabcoinrpc -rpcpassword=P0 -rpcconnect=localhost:${port_rpc} createcontract   2500000 0.000001 `

export ADDR_SC_4=`echo ${SC_4}|jq '.address'`
echo "node 4 create contract..."
echo $SC_4
echo

sleep 5
fab_mining_one 5 $FAB_ROOT $RPC_PORT 25 
sleep 5

fab_bal $DAEMON_NUM 


#### END Case : create contract #######################


echo
echo

echo "Test: deposit to contract =================================================="
echo "contract address is " ${ADDR_SC_4}
port_rpc=$(($RPC_PORT + 4 ))

cat client_command.template|sed -e "s?FAB_ROOT?${FAB_ROOT}?"|sed -e "s?PORT_RPC?${port_rpc}?"|sed -e "s?CLIENTCOMMAND?sendtocontract?"|sed -e "s?PARAMLIST?${ADDR_SC_4}  d0e30db0 10?" > tmp_send_contract.sr
echo "node 4 deposit 10 coin to contract "
bash tmp_send_contract.sr
sleep 1

fab_bal $DAEMON_NUM 
echo

port_rpc=$(($RPC_PORT + 1 ))

cat client_command.template|sed -e "s?FAB_ROOT?${FAB_ROOT}?"|sed -e "s?PORT_RPC?${port_rpc}?"|sed -e "s?CLIENTCOMMAND?sendtocontract?"|sed -e "s?PARAMLIST?${ADDR_SC_4}  d0e30db0 5?" > tmp_send_contract.sr
echo "node 1 deposit 5 coin to contract "
bash tmp_send_contract.sr
sleep 1

fab_bal $DAEMON_NUM 
echo
fab_mining_one 5 $FAB_ROOT $RPC_PORT 25 
sleep 2
fab_bal $DAEMON_NUM 
#### END Case : deposit to contract #######################

echo
echo

echo "Test: withdraw from contract =================================================="
echo "contract address is " ${ADDR_SC_4}
echo

port_rpc=$(($RPC_PORT + 1 ))

cat client_command.template|sed -e "s?FAB_ROOT?${FAB_ROOT}?"|sed -e "s?PORT_RPC?${port_rpc}?"|sed -e "s?CLIENTCOMMAND?sendtocontract?"|sed -e "s?PARAMLIST?${ADDR_SC_4} 3ccfd60b 0 190000 0.0000005 ${ADDR_4}?" > tmp_send_contract.sr
echo "node 1 withdraw 5 coin from contract TO NDOE 4 "
bash tmp_send_contract.sr
sleep 1

fab_bal $DAEMON_NUM 
echo
fab_mining_one 5 $FAB_ROOT $RPC_PORT 25 
sleep 2
fab_bal $DAEMON_NUM 
#### END Case : withdraw from contract #######################
